import { expect } from 'chai';
import request from 'supertest';
import app from '../app';

describe('404 error', () => {
  it('should show a 404 error, with prepared message if page does not exist', done => {
    request(app)
      .get('/quotes')
      .end((err, res) => {
        expect(res.status).to.eql(404);
        expect(res.body.message).to.eql(`Can't find /quotes on this server`);
        done(err);
      });
  });
});

import express from 'express';
import logger from 'morgan';
import { config } from 'dotenv';
import movieRouter from './routes/movie.routes';
import charRouter from './routes/characters.routes';

config();

const app = express();

if (app.get('env') === 'development') {
  app.use(logger('dev'));
}

app.use('/movie', movieRouter);
app.use('/character', charRouter);

//404 Page
app.all('*', (req, res, next) => {
  res.status(404).json({
    status: 'error',
    message: `Can't find ${req.originalUrl} on this server`
  });
});

const port = process.env.NODE_ENV === 'test' ? 5272 : process.env.PORT || 3000;

app.listen(port, () => {
  console.log(`server listening on port ${port}`);
});

export default app;

import { Router } from 'express';
import movieController from '../controllers/movie.controller';

const movieRouter = Router();

movieRouter.get('/', movieController.getMovies);

export default movieRouter;

import { config } from 'dotenv';
import { sortBy } from '../utils/sort';
import format from '../utils/format';
import axiosRequest from '../utils/request';

config();

const movieController = {};

movieController.getMovies = async (req, res) => {
  try {
    const response = await axiosRequest('movie');

    const movies = response.data;
    if (req.query.sort) {
      movies.docs.sort(sortBy(req.query.sort));
    }
    format(movies.docs);
    res.status(200).json({
      status: 'success',
      data: movies
    });
  } catch (err) {
    res.status(400).json({
      status: 'error',
      error: err
    });
  }
};

export default movieController;

export const sortBy = val => {
  let prop = val;
  let order = 1;

  if (prop[0] === '-') {
    order = -1;
    prop = prop.substr(1);
  }
  if (prop === 'revenue') {
    prop = 'boxOfficeRevenueInMillions';
  } else if (prop === 'budget') {
    prop = 'budgetInMillions';
  } else if (prop === 'runtime') {
    prop = 'runtimeInMinutes';
  }
  return (a, b) => {
    if (a[prop] < b[prop]) {
      return -1 * order;
    }
    if (a[prop] > b[prop]) {
      return 1 * order;
    }
    return 0;
  };
};

export const a = 'b';

const numberWithCommas = x =>
  x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');

const convertToMillionNaira = val => val * 1000000 * 350;

const format = arr => {
  return arr.forEach(entry => {
    entry.budgetInMillions = `₦${numberWithCommas(
      convertToMillionNaira(entry.budgetInMillions)
    )}`;
    entry.boxOfficeRevenueInMillions = `₦${numberWithCommas(
      convertToMillionNaira(entry.boxOfficeRevenueInMillions)
    )}`;
  });
};

export default format;

import axios from 'axios';
import { config } from 'dotenv';

config();

const headers = {
  Authorization: `Bearer ${process.env.API_KEY}`
};

const axiosRequest = async param => {
  try {
    return await axios({
      url: `${process.env.URL}/${param}`,
      headers
    });
  } catch (err) {
    return err;
  }
};

export default axiosRequest;

import { Router } from 'express';
import charController from '../controllers/characters.controller';

const charRouter = Router();

charRouter.get('/', charController.getCharacters);

export default charRouter;

import { config } from 'dotenv';
import { sortBy } from '../utils/sort';
import paginate from '../utils/pagination';
import axiosRequest from '../utils/request';

config();

const charController = {};

charController.getCharacters = async (req, res) => {
  try {
    const response = await axiosRequest('character');
    const characters = response.data;
    if (req.query.sort === 'race' || req.query.sort === 'gender') {
      characters.docs.sort(sortBy(req.query.sort));
    }
    //Paginate Results
    const limit = req.query.limit || 100;
    const pageNumber = req.query.page || 1;
    const data = paginate(characters.docs, pageNumber, limit);
    // console.log(characters.docs.length);

    res.status(200).json({
      status: 'success',
      data
    });
  } catch (err) {
    res.status(400).json({
      status: 'error',
      error: err
    });
  }
};

export default charController;

const paginate = (arr, pageNumber, limit) => {
  const pages = {};
  pages.current = pageNumber;
  pages.prev = pageNumber > 1 ? pageNumber - 1 : null;
  pages.hasPrev = pages.prev !== null;
  pages.total = Math.ceil(arr.length / limit);
  pages.next = pageNumber >= pages.total ? null : pageNumber + 1;
  pages.hasNext = pages.next !== null;
  //arrays are zero based;

  pageNumber -= 1;
  const items = {};
  items.begin = limit * pageNumber + 1;
  items.end = (pageNumber + 1) * limit;
  items.total = arr.length;

  const data = {
    docs: arr.slice(items.begin - 1, items.end),
    pages,
    items
  };

  if (items.end > items.total) {
    items.end = items.total;
  }
  if (items.begin > items.total) {
    items.begin = items.total;
  }
  return data;
};

export default paginate;

import { expect } from 'chai';
import request from 'supertest';
import app from '../../app';

describe('Character Route', function() {
  this.timeout(3000);
  it('should successfully fetch and paginate characters', done => {
    request(app)
      .get(`/character?limit=10&sort=race`)
      .set('Authorization', `Bearer ${process.env.API_KEY}`)
      .end((err, res) => {
        const { status, data } = res.body;
        expect(res.status).to.eql(200);
        expect(status).to.eql('success');
        expect(data).to.be.an('object');
        expect(data).to.have.property('docs');
        expect(data.docs).to.be.an('array');
        expect(data.docs).to.have.lengthOf(10);
        expect(data.docs[0].race).to.eql('');
        done(err);
      });
  });
  it('should successfully paginate characters into pages', done => {
    request(app)
      .get(`/character?limit=10&page=2`)
      .set('Authorization', `Bearer ${process.env.API_KEY}`)
      .end((err, res) => {
        const { status, data } = res.body;
        expect(res.status).to.eql(200);
        expect(status).to.eql('success');
        expect(data).to.be.an('object');
        expect(data).to.have.property('docs');
        expect(data.docs).to.be.an('array');
        expect(data.docs).to.have.lengthOf(10);
        expect(data.pages.current).to.be.eql('2');
        expect(data.items.begin).to.be.eql(11);
        expect(data.items.end).to.be.eql(20);
        done(err);
      });
  });
});

import { expect } from 'chai';
import request from 'supertest';
import app from '../../app';

describe('Movie Route', () => {
  it('should successfully fetch, format and sort movie data', done => {
    request(app)
      .get(`/movie?sort=budget`)
      .set('Authorization', `Bearer ${process.env.API_KEY}`)
      .end((err, res) => {
        const { status, data } = res.body;
        expect(res.status).to.eql(200);
        expect(status).to.eql('success');
        expect(data).to.be.an('object');
        expect(data).to.have.property('docs');
        expect(data.docs).to.be.an('array');
        expect(data.docs).to.have.lengthOf(8);
        expect(data.docs[0]).to.have.property('name');
        expect(data.docs[0]).to.have.property('runtimeInMinutes');
        expect(data.docs[0].budgetInMillions).to.include('₦');
        expect(data.docs[0].budgetInMillions).to.be.eql('₦32,550,000,000');
        expect(data.docs[data.docs.length - 1].budgetInMillions).to.be.eql(
          '₦236,250,000,000'
        );
        done(err);
      });
  });
  it('should sort movie data in descending order', done => {
    request(app)
      .get(`/movie?sort=-runtime`)
      .set('Authorization', `Bearer ${process.env.API_KEY}`)
      .end((err, res) => {
        const { status, data } = res.body;
        expect(res.status).to.eql(200);
        expect(status).to.eql('success');
        expect(data.docs).to.be.an('array');
        expect(data.docs[0].runtimeInMinutes).to.be.eql(558);
        expect(data.docs[data.docs.length - 1].runtimeInMinutes).to.be.eql(144);
        done(err);
      });
  });
});
